k1 = 2m1 = 2n1 = 2def dom_prob(k,m,n):    s = sum([k,m,n])    s2 = s - 1    dom = k/s    het = m/s    rec = n/s        dHet = 0.5 * het    rHet_dom = (0.5*het) * (k/s2)    rHet_dHet = (0.5*het) * (0.5*(((m-1)/s2)))    sHet = sum([dHet, rHet_dom, rHet_dHet])        rec_dom = rec * (k/s2)    rec_dHet = rec * (0.5*((m/s2)))    sRec = rec_dom + rec_dHet        return sum([dom, sHet, sRec])print(dom_prob(k1,m1,n1))